{
  "version": "0.2",
  "phases": {
    "install": {
      "runtime-versions": {
        "nodejs": "20.x",
        "python": "3.12"
      }
    },
    "build": {
      "commands": [
        "cd \"infrastructure\"",
        "curl -LsSf https://astral.sh/uv/install.sh | sh",
        "export PATH=$HOME/.local/bin:$PATH",
        "uv sync",
        "npm install -g aws-cdk",
        "uv run cdk synth --app 'python pipeline_app.py'"
      ]
    }
  },
  "artifacts": {
    "base-directory": "infrastructure/cdk.out",
    "files": [
      "**/*"
    ]
  }
}
